namespace FrostAura.MCP.Gaia.Enums;

/// <summary>
/// Enumeration of log levels used throughout the application
/// </summary>
public enum LogLevel
{
    /// <summary>
    /// Debug level logging
    /// </summary>
    Debug = 0,

    /// <summary>
    /// Information level logging
    /// </summary>
    Information = 1,

    /// <summary>
    /// Warning level logging
    /// </summary>
    Warning = 2,

    /// <summary>
    /// Error level logging
    /// </summary>
    Error = 3,

    /// <summary>
    /// Critical level logging
    /// </summary>
    Critical = 4
}
